import React, { Component } from 'react'
import './Home.css'
class Home extends Component {
    state = {
        // Make loading false
        loading: true,
        city: '',
        data: '',
        icon: '',
    }
    handleChange = (e) => {
        this.setState({
            city: e.target.value
        })
    }

    handleUnixToTime = (num) => {
        let unix_timestamp = num;
        var date = new Date(unix_timestamp * 1000);
        var hours = date.getHours();
        var minutes = "0" + date.getMinutes();
        var seconds = "0" + date.getSeconds();
        var formattedTime = hours + ':' + minutes.substr(-2) + ':' + seconds.substr(-2);
        return formattedTime;
    }

    handleUnixToDate = (num) => {
        var months_arr = ['January','February','March','April','May','June','July','August','September','October','November','December'];
        var date = new Date(num*1000);

    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.city);
        let api = `https://cors-anywhere.herokuapp.com/api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=4df788ff6705f12d16388011a02c22b5`;
        fetch(api).then(response => {
            // converting data to json
            return response.json();
        }).catch(function () {
            console.log("Error Occured!")
        }).then(data => {
            console.log('data', data);
            let icon = `http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`
            console.log(icon);
            console.log('Description', data.weather[0].description);
            console.log('Icon', data.weather[0].icon);
            let temp = data.dt;
            let date = new Date(temp * 1000);
            console.log('Date', date.getDate());
            console.log('Month', date.getMonth());
            console.log('Day', date.getDay());
            console.log('Year', date.getYear());


            this.setState({
                data: data,
                loading: false,
                icon: icon,
                date: date,
                sunrise: this.handleUnixToTime(data.sys.sunrise)
            })
        });
        this.setState({
            city: ''
        });
    }

    render() {
        return (
            <div className="hero_section">
                <div className="container">
                    <div className="input-container">
                        <form onSubmit={this.handleSubmit}>
                            <input className="input-city" value={this.state.city} onChange={this.handleChange} placeholder="Enter City" />
                        </form>
                    </div>
                    <div className="output-container">
                        {this.state.loading ? <div></div> :
                            <div className="output">
                                <div className="left">
                                    <div className='location-time'>
                                        <div className="location">{this.state.data.name}, {this.state.data.sys.country}</div>
                                        <div className="time">Saturday, 31 Oct (Hardcore)</div>
                                    </div>
                                    <div className='logo-temp'>
                                        <div className="icon-flex">
                                            <img src={this.state.icon} Alt='Icon' />
                                        </div>
                                        <div className="description-flex">
                                            <div className="output-temp data">{this.state.data.main.temp} K</div>
                                            <div className="icon-description data">{this.state.data.weather[0].description}</div>
                                        </div>
                                    </div>
                                </div>
                                <div className="right">
                                    <div className="ele_right">
                                        <div className>Wind Speed</div>
                                        <div>{this.state.data.wind.speed}</div>
                                    </div>
                                    <div className="ele_right">
                                        <div>Humidity</div>
                                        <div>{this.state.data.main.humidity}</div>
                                    </div>
                                    <div className="ele_right">
                                        <div>Sunrise</div>
                                        {/* Trial */}
                                        <div>{this.state.sunrise}</div>
                                    </div>
                                    <div className="ele_right">
                                        <div>Sunset</div>
                                        <div>{this.state.data.sys.sunset}</div>
                                    </div>
                                    <div className="ele_right">
                                        <div>Pressure</div>
                                        <div>{this.state.data.main.pressure} P </div>
                                    </div>
                                    <div className="ele_right">
                                        <div>Visibility</div>
                                        <div>{this.state.data.visibility}</div>
                                    </div>
                                </div>
                            </div>
                        }
                    </div>
                </div>

            </div>
        )
    }
}
export default Home
