import React, { Component } from 'react'
import './Home.css'
class Home extends Component {
    state = {
        // Make loading false
        loading: false,
        city: '',
        data: '',
        icon: '',
    }
    handleChange = (e) => {
        this.setState({
            city: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.city);
        let api = `https://cors-anywhere.herokuapp.com/api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=4df788ff6705f12d16388011a02c22b5`;
        fetch(api).then(response => {
            // converting data to json
            return response.json();
        }).catch(function () {
            console.log("Error Occured!")
        }).then(data => {
            console.log('data', data);
            let icon = `http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`
            console.log(icon);
            console.log('Description', data.weather[0].description);
            console.log('Icon', data.weather[0].icon);

            this.setState({
                data: data,
                loading: false,
                icon: icon
            })
        });
        this.setState({
            city: ''
        });
    }

    render() {
        return (
            <div className="hero_section">
                <div className="container">
                    <div className="input-container">
                        <form onSubmit={this.handleSubmit}>
                            <input className="input-city" value={this.state.city} onChange={this.handleChange} placeholder="Enter City" />
                        </form>
                    </div>
                    <div className="output-container">
                        {this.state.loading ? <div></div> :
                            <div className="output">
                                <div className="left">
                                    <div className='location-time'>
                                        <div className="location">Udaipur, IN</div>
                                        <div className="time">Saturday, 31 Oct</div>
                                    </div>
                                    <div className='logo-temp'>
                                    <div className="icon-flex">
                                        <img src='' Alt='Icon' />
                                    </div>
                                        <div className="description-flex">
                                        <div className="output-temp data">301.1 K</div>
                                        <div className="icon-description data">Haze</div>
                                        </div>
                                    </div>
                                </div>
                                <div className="right">
                                    <div className="ele_right">
                                        <div>Wind Speed</div>
                                        <div>HI</div>
                                    </div>
                                    <div className="ele_right">Humidity: </div>
                                    <div className="ele_right">Sunrise: </div>
                                    <div className="ele_right">Sunset: </div>
                                    <div className="ele_right">Pressure: </div>
                                    <div className="ele_right">Visibility: </div>

                                </div>
                            </div>
                        }
                    </div>
                </div>

            </div>
        )
    }
}
export default Home
