import React, { Component } from 'react'
import './Home.css'
class Home extends Component {
    state = [{
        loading: true,
        city: '',
        data: '',
        icon: ''
    }]
    handleChange = (e) => {
        this.setState({
            city: e.target.value
        })
    }

    // let proxy = 'https://cors-anywhere.herokuapp.com/';
    // let api = `api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=4df788ff6705f12d16388011a02c22b5`;
    // fetch(api).then(response => {
    //     // converting data to json
    //     return response.json();
    //         }).then(data => {
    //         console.log('data', data);
    //     })

handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state.city);
    let api = `https://cors-anywhere.herokuapp.com/api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=4df788ff6705f12d16388011a02c22b5`;
    fetch(api).then(response => {
        // converting data to json
        return response.json();
            }).then(data => {
            console.log('data', data);
            let icon = `http://openweathermap.org/img/wn/${data.weather.icon}@2x.png`
            this.setState({
                data: data,
                loading: false,
            })
        });
    this.setState({
        city: ''
    });
}

render(){
    return (
        <div className="hero_section">
            <div className="container">
                <div className="input-container">
                    <form onSubmit={this.handleSubmit}>
                        <input className="input-city" value={this.state.city} onChange={this.handleChange} placeholder="Enter City" />
                    </form>
                </div>
                <div className="output-container">
                    <div className="output">{this.data.main.temp} K</div>
                    <img src={icon} />
                    <div className="icon description">{this.data.weather.description}</div>
                </div>
            </div>
        </div>
    )
}
}
export default Home
